import { useState } from 'react';
import Toast from 'react-bootstrap/Toast';

const [showSuccess, setShowSuccess] = useState({ visible: false, message: null });
const [showError, setShowError] = useState({ visible: false, message: null });

export const doSomethingWithInput = (theInput) => {
    //Do something with the input
    return theInput;
};

export const justAnAlert = () => {
    alert('hello');
};

export const showSuccessCall = (message) => {
    setShowSuccess({ visible: true, message: message })
    setTimeout(function () {
        setShowSuccess({ visible: false })
        window.location.reload();
    }, 2000);
    return (<Toast show={showSuccess.visible} style={{ backgroundImage: 'linear-gradient(#4BB543,#87cc80)', color: 'white', fontSize: '15px', borderLeft: '4px solid green' }}>
        <Toast.Body><img style={{ padding: '8px' }} src="/sevkin/assets/img/icon/success.png" alt="sevkin" height={50} width={50} />&nbsp;<b>{showSuccess.message}</b></Toast.Body>
    </Toast>)
};
export const showErrorCall = (message) => {
    setShowError({ visible: true, message: message })
    setTimeout(function () {
        setShowError({ visible: false })
        window.location.reload();
    }, 2000);
    return (<Toast show={showError.visible} style={{ backgroundImage: 'linear-gradient(#DC3545,#ee7b7b)', color: 'white', fontSize: '15px', borderLeft: '4px solid red' }}>
        <Toast.Body><img src="/sevkin/assets/img/icon/error.png" alt="sevkin" height={50} width={50} /><b>{showError.message}</b></Toast.Body>
      </Toast>)
};
