          <Form.Group
            className="mb-3"
            controlId="exampleForm.ControlTextarea1">
            <div className='row'>
              <div className='col-10'>
                <Form.Control as="textarea" rows={1} placeholder='Search Content...' />
              </div>
              <div className='col-2'>
                <a href='#!' className="boxed-btn"><img className="icon-style"
