import { useState,useEffect } from 'react';
import Toast from 'react-bootstrap/Toast';

export const doSomethingWithInput = (theInput) => {
    //Do something with the input
    return theInput;
};

export const justAnAlert = () => {
    alert('hello');
};

export const ShowSuccessCall = (message) => {
    const [showSuccess, setShowSuccess] = useState(false)
    setShowSuccess(true)
    setTimeout(function () {
        setShowSuccess(false)
        window.location.reload();
    }, 2000);
    <Toast show={showSuccess} style={{ backgroundImage: 'linear-gradient(#4BB543,#87cc80)', color: 'white', fontSize: '15px', borderLeft: '4px solid green' }}>
        <Toast.Body><img style={{ padding: '8px' }} src="/sevkin/assets/img/icon/success.png" alt="sevkin" height={50} width={50} />&nbsp;<b>{message}</b></Toast.Body>
    </Toast>
};
export const ShowErrorCall = (message) => {
    const [showError, setShowError] = useState(false)
    setShowError(true)
    setTimeout(function () {
        setShowError(false)
        window.location.reload();
    }, 2000);
    <Toast show={showError} style={{ backgroundImage: 'linear-gradient(#DC3545,#ee7b7b)', color: 'white', fontSize: '15px', borderLeft: '4px solid red' }}>
        <Toast.Body><img src="/sevkin/assets/img/icon/error.png" alt="sevkin" height={50} width={50} /><b>{message}</b></Toast.Body>
    </Toast>
};

export const ShowErrorToast = ({ message }) => { // Renamed for clarity and to indicate it's a component
    const [showError, setShowError] = useState(true); // Start as true to immediately show the toast

    useEffect(() => {
        // Set a timeout to hide the toast and reload the page after 2 seconds
        const timer = setTimeout(() => {
            setShowError(false);
            window.location.reload();
        }, 2000);

        // Cleanup function: This runs if the component unmounts before the timer finishes
        // or if the effect re-runs (though in this case, it's a one-time effect)
        return () => clearTimeout(timer);
    }, []); // Empty dependency array ensures this effect runs only once on mount

    // Render the Toast component
    return (
        <Toast
            show={showError}
            onClose={() => setShowError(false)} // Optional: allow user to close manually
            style={{
                position: 'fixed', // Position the toast
                top: '20px',
                right: '20px',
                zIndex: 9999, // Ensure it's above other content
                backgroundImage: 'linear-gradient(#DC3545,#ee7b7b)',
                color: 'white',
                fontSize: '15px',
                borderLeft: '4px solid red'
            }}
        >
            <Toast.Body>
                <img
                    src="/sevkin/assets/img/icon/error.png"
                    alt="sevkin"
                    height={50}
                    width={50}
                    style={{ marginRight: '10px' }} // Add some spacing
                />
                <b>{message}</b>
            </Toast.Body>
        </Toast>
    );
}
